import React, { useEffect, useState } from "react";
import ApiService from "../../ApiService";
import { Container, Row, Col, Figure, Form } from "react-bootstrap";

export default function UserInfoForm(props) {
  const [first_name, set_first_name] = useState(props.userInfo.first_name);
  const [last_name, set_last_name] = useState(props.userInfo.last_name);
  const [house_name, set_house_name] = useState(props.userInfo.house_name);
  const [road_no, set_road_no] = useState(props.userInfo.road_no);
  const [block_no, set_block_no] = useState(props.userInfo.block_no);
  const [area, set_area] = useState(props.userInfo.area);
  const [city, set_city] = useState(props.userInfo.city);
  const [district, set_district] = useState(props.userInfo.district);
  const [mobilePhone, set_mobilePhone] = useState(props.userInfo.mobilePhone);

  const allNull = () => {
    set_first_name("");
    set_last_name("");
    set_house_name("");
    set_road_no("");
    set_block_no("");
    set_area("");
    set_city("");
    set_district("");
    set_mobilePhone("");
  };

  useEffect(() => {
    set_first_name(props.userInfo.first_name);
    set_last_name(props.userInfo.last_name);
    set_house_name(props.userInfo.house_name);
    set_road_no(props.userInfo.road_no);
    set_block_no(props.userInfo.block_no);
    set_area(props.userInfo.area);
    set_city(props.userInfo.city);
    set_district(props.userInfo.district);
    set_mobilePhone(props.userInfo.mobilePhone);
  }, [props.userInfo]);

  const UpdateUserInfo = () => {
    ApiService.UpdateUserInfo(props.userInfo.id, {
      first_name,
      last_name,
      house_name,
      road_no,
      block_no,
      area,
      city,
      district,
      mobilePhone,
    })
      .then((resp) => props.updatedInformation(resp))
      .then(allNull());
  };

  const [validated, setValidated] = useState(false);
  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  const onSubmit = (e) => {
    handleSubmit(e);
    e.preventDefault();
    if (password === re_password && password && re_password) {
      signup(
        first_name,
        last_name,
        email,
        house_name,
        road_no,
        block_no,
        area,
        city,
        district,
        mobilePhone,
        is_worker,
        password,
        re_password
      );
      setAccountCreated(true);
    }
    // else return alert("Password's doesn't match!!");
  };
  return (
    <Container fluid>
      {props.userInfo ? (
        <>
          <Row className="text-center">Update User Info Form</Row>
          <Row className="mb-4">
            <Col sm></Col>
            <Col sm={4} className="justify-content-center">
              <Form
                noValidate
                validated={validated}
                onSubmit={(e) => onSubmit(e)}
                // className="text-center mb-2"
              >
                <Row>
                  <Form.Group as={Col} md="6">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="first_name"
                        value={first_name}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">First Name</label>
                    </Form.Floating>
                  </Form.Group>
                  <Form.Group as={Col} md="6">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="last_name"
                        value={last_name}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">Last Name</label>
                    </Form.Floating>
                  </Form.Group>
                </Row>
                <Row>
                  <Form.Group as={Col} md="4">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="house_name"
                        value={house_name}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">House name</label>
                    </Form.Floating>
                  </Form.Group>
                  <Form.Group as={Col} md="4">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="road_no"
                        value={road_no}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">Road no</label>
                    </Form.Floating>
                  </Form.Group>
                  <Form.Group as={Col} md="4">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="block_no"
                        value={block_no}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">Block no</label>
                    </Form.Floating>
                  </Form.Group>
                </Row>
                <Row>
                  <Form.Group as={Col} md="4">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="area"
                        value={area}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">Area</label>
                    </Form.Floating>
                  </Form.Group>
                  <Form.Group as={Col} md="4">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="city"
                        value={city}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">City</label>
                    </Form.Floating>
                  </Form.Group>
                  <Form.Group as={Col} md="4">
                    <Form.Floating className="mb-2">
                      <Form.Control
                        className=" rounded-3"
                        type="text"
                        placeholder="name"
                        name="district"
                        value={district}
                        onChange={(e) => onChange(e)}
                        required
                      />
                      <label htmlFor="floatingInputCustom">District</label>
                    </Form.Floating>
                  </Form.Group>
                </Row>
                <Form.Group>
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="mobilePhone"
                      value={mobilePhone}
                      onChange={(e) => onChange(e)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Mobile Phone</label>
                  </Form.Floating>
                </Form.Group>

                <Button
                  variant="danger"
                  type="submit"
                  className=" rounded-3 text-white fw-bold fs-4"
                  style={{
                    // height: "30%",
                    width: "100%",
                  }}
                >
                  Update
                </Button>
              </Form>
            </Col>
          </Row>
        </>
      ) : null}
    </Container>
  );
}
