import React, { useState } from "react";
import { Link, Navigate } from "react-router-dom";
import { connect } from "react-redux";
import { signup } from "../../redux/actions/auth";
import Login from "./Login";

import { Form, Container, Row, Col, Button } from "react-bootstrap";

const Signup = ({ signup, isAuthenticated }) => {
  const [validated, setValidated] = useState(false);

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  const [accountCreated, setAccountCreated] = useState(false);
  const [is_worker, setIs_worker] = useState(false);
  // const [profile_picture, setProfile_picture] = useState();
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    house_name: "",
    road_no: "",
    block_no: "",
    area: "",
    city: "",
    district: "",
    mobilePhone: "",
    password: "",
    re_password: "",
  });

  const handleOnChange = () => {
    setIs_worker(!is_worker);
  };

  const {
    first_name,
    last_name,
    email,
    house_name,
    road_no,
    block_no,
    area,
    city,
    district,
    mobilePhone,
    password,
    re_password,
  } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    handleSubmit(e);
    e.preventDefault();
    if (password === re_password) {
      signup(
        first_name,
        last_name,
        email,
        house_name,
        road_no,
        block_no,
        area,
        city,
        district,
        mobilePhone,
        is_worker,
        password,
        re_password
      );
      setAccountCreated(true);
    }
    // else return alert("Password's doesn't match!!");
  };

  if (isAuthenticated) {
    return <Navigate to="/" />;
  }
  if (accountCreated) {
    return <Login isSignUp={accountCreated} />;
  }

  return (
    <div className="container mt-5">
      <h1>Sign Up</h1>
      <p>Create your Account</p>
      <form onSubmit={(e) => onSubmit(e)}>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="First Name"
            name="first_name"
            value={first_name}
            onChange={(e) => onChange(e)}
            required
          />
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="Last Name"
            name="last_name"
            value={last_name}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">Last Name</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="House name / number*"
            name="house_name"
            value={house_name}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">House name / number</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="Road no*"
            name="road_no"
            value={road_no}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">Road no</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="Block no*"
            name="block_no"
            value={block_no}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">Block no</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="area*"
            name="area"
            value={area}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">Area</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="city*"
            name="city"
            value={city}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">City</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="district*"
            name="district"
            value={district}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">District</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="text"
            placeholder="mobilePhone*"
            name="mobilePhone"
            value={mobilePhone}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">Mobile Phone</label>
        </div>
        {/* <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="file"
            // placeholder="profile_picture*"
            // name="profile_picture"
            onChange={(e) => setProfile_picture(e.target.files[0])}
          />
        </div> */}

        <div className="form-group">
          <input
            className="form-control"
            id="floatingInput"
            type="email"
            placeholder="Email*"
            name="email"
            value={email}
            onChange={(e) => onChange(e)}
            required
          />
          <label for="floatingInput">Email</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingPassword"
            type="password"
            placeholder="Password*"
            name="password"
            value={password}
            onChange={(e) => onChange(e)}
            minLength="6"
            required
          />
          <label for="floatingPassword">Password</label>
        </div>
        <div className="form-group">
          <input
            className="form-control"
            id="floatingPassword"
            type="password"
            placeholder="Confirm Password*"
            name="re_password"
            value={re_password}
            onChange={(e) => onChange(e)}
            minLength="6"
            required
          />
          <label for="floatingPassword">Confirm Password</label>
        </div>
        <div className="form-group form-check form-switch">
          <input
            className="form-check-input"
            type="checkbox"
            id="flexSwitchCheckDefault"
            value={is_worker}
            onChange={handleOnChange}
          />
          <label className="form-check-label" for="flexSwitchCheckDefault">
            Are you a worker?
          </label>
        </div>
        <button className="btn btn-primary" type="submit">
          Register
        </button>
      </form>
      {/* <button className="btn btn-danger mt-3" onClick={continueWithGoogle}>
        Continue With Google
      </button>
      <br />
      <button className="btn btn-primary mt-3" onClick={continueWithFacebook}>
        Continue With Facebook
      </button> */}
      <p className="mt-3">
        Already have an account? <Link to="/login">Sign In</Link>
      </p>
    </div>
  );
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { signup })(Signup);
