import React, { useState, useEffect } from "react";

import { Link } from "react-router-dom";
import { Row, Col, Figure } from "react-bootstrap";

import ToolkitProvider, {
  Search,
} from "react-bootstrap-table2-toolkit/dist/react-bootstrap-table2-toolkit";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import filterFactory, { textFilter } from "react-bootstrap-table2-filter";

export default function ShopHasDrugs(props) {
  const [shop, setShop] = useState([]);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/gk/shop/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `JWT ${localStorage.getItem("access")}`,
        Accept: "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((resp) => setShop(resp))
      .catch((error) => console.log(error));
  }, []);
  const { SearchBar, ClearSearchButton } = Search;

  const columns = [
    {
      dataField: "shopName",
      text: "Shop Name",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      sort: true,
      filter: textFilter(),
    },
    {
      dataField: "detailedAddr",
      text: "Address",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },

      sort: true,
      filter: textFilter(),
    },
    {
      dataField: "review",
      text: "Ratting",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      sort: true,
      filter: textFilter(),
    },
  ];

  const rowStyle = (row, rowIndex) => {
    const style = {};
    if (rowIndex % 2 == 0) {
      style.backgroundColor = "#c95f5f";
    } else {
      style.backgroundColor = "#bf3636";
    }
    style.textAlign = "center";
    style.color = "white";
    return style;
  };

  const pagination = paginationFactory({
    page: 1,
    sizePerPage: 10,
    lastPageText: ">>",
    firstPageText: "<<",
    nextPageText: ">",
    prePageText: "<",
    showTotal: true,
    alwaysShowAllBtns: false,
    onPageChange: function (page, sizePerPage) {
      console.log("page", page);
      console.log("sizePerPage", sizePerPage);
    },
    onSizePerPageChange: function (page, sizePerPage) {
      console.log("page", page);
      console.log("sizePerPage", sizePerPage);
    },
  });

  return (
    <div>
      <ToolkitProvider keyField="id" data={shop} columns={columns} search>
        {(props) => (
          <div className="p-2">
            <Row>
              <br />
            </Row>
            <Row>
              <Col sm={2} />
              <Col
                sm
                className="shadow-lg p-4 rounded-3 text-center text-white "
                style={{
                  background: "#FFBD2D",
                }}
              >
                <span>
                  <SearchBar
                    srText=""
                    placeholder="Search Drugs In This Shop"
                    {...props.searchProps}
                  />
                  <span> </span>
                  <ClearSearchButton
                    className="btn btn-secondary btn-sm"
                    {...props.searchProps}
                  />
                </span>
              </Col>
              <Col sm={2} />
            </Row>
            <Row>
              <br />
            </Row>
            <Row className=" p-4 rounded-3 text-center text-white">
              <BootstrapTable
                pagination={pagination}
                filter={filterFactory()}
                bordered={false}
                rowStyle={rowStyle}
                {...props.baseProps}
              />
            </Row>
          </div>
        )}
      </ToolkitProvider>
    </div>
  );
}
