import React from "react";

export default function UserInfoForm(userInfo) {
  const [first_name, set_first_name] = useState(userInfo.first_name);
  const [last_name, set_last_name] = useState(userInfo.last_name);
  const [house_name, set_house_name] = useState(userInfo.house_name);
  const [road_no, set_road_no] = useState(userInfo.road_no);
  const [block_no, set_block_no] = useState(userInfo.block_no);
  const [area, set_area] = useState(userInfo.area);
  const [city, set_city] = useState(userInfo.city);
  const [district, set_district] = useState(userInfo.district);
  const [mobilePhone, set_mobilePhone] = useState(userInfo.mobilePhone);

  const allNull = () => {
    set_first_name("");
    set_last_name("");
    set_house_name("");
    set_road_no("");
    set_block_no("");
    set_area("");
    set_city("");
    set_district("");
    set_mobilePhone("");
  };

  useEffect(() => {
    set_first_name(userInfo.first_name);
    set_last_name(userInfo.last_name);
    set_house_name(userInfo.house_name);
    set_road_no(userInfo.road_no);
    set_block_no(userInfo.block_no);
    set_area(userInfo.area);
    set_city(userInfo.city);
    set_district(userInfo.district);
    set_mobilePhone(userInfo.mobilePhone);
  }, [userInfo]);

  const UpdateUserInfo = () => {
    APIService.UpdateUserInfo(userInfo.id, {
      first_name,
      last_name,
      house_name,
      road_no,
      block_no,
      area,
      city,
      district,
      mobilePhone,
    })
      .then((resp) => props.updatedInformation(resp))
      .then(allNull());
  };
  return <div>UserInfoForm</div>;
}
