import React, { useState, useEffect } from "react";
import { Link, Navigate, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import ResetPassword from "../containers/LogInAndSignUp/ResetPassword";
import { Form, Container, Row, Col, Button, Figure } from "react-bootstrap";

const UserInfo = ({ isAuthenticated }) => {
  const [user, setUser] = useState([]);
  const [userDataEdit, setUserDataEdit] = useState([]);
  const navigate = useNavigate();

  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `JWT ${localStorage.getItem("access")}`,
      Accept: "application/json",
    },
  };

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/auth/users/me/`, config)
      .then((res) => setUser(res.data))
      .then(console.log(user))
      .catch((error) => console.log(error));
  }, [isAuthenticated]);

  useEffect(() => {
    axios
      .get(
        `${process.env.REACT_APP_API_URL}/accounts/users/${user.id}/`,
        config
      )
      .then((res) => setUserDataEdit(res.data))
      .catch((error) => console.log(error));
  }, []);

  console.table(userDataEdit);

  const reset_password_btn_click = () => {
    navigate("/reset-password");
  };

  return (
    <div className="text-center">
      {userDataEdit.is_worker ? (
        <h1>User is worker</h1>
      ) : (
        <h1>User is not worker</h1>
      )}
      <br /> <br /> <br />
      <h1>First name: {user.first_name}</h1>
      <h1>Last name: {user.last_name}</h1>
      <br /> <br />
      {user.is_staff ? <h1>User is staff</h1> : <h1>User is not staff</h1>}
      <br /> <br />
      <h1>Last login:{user.last_login}</h1>
      <Button onClick={reset_password_btn_click}>
        Reset your password here
      </Button>
    </div>
  );
};

export default UserInfo;
