import { useState, useEffect } from "react";
import ApiService from "../../ApiService";
import MyShopItemForm from "./MyShopItemForm";

import { Link, useLocation, useNavigate } from "react-router-dom";
import UpdateOutlinedIcon from "@mui/icons-material/UpdateOutlined";
import { Container, Row, Col, Figure, ProgressBar } from "react-bootstrap";

import Grid from "@material-ui/core/Grid";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { Button, CardActionArea, CardActions } from "@mui/material";

import Rating from "@mui/material/Rating";
import FavoriteIcon from "@mui/icons-material/Favorite";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import DeleteForeverOutlinedIcon from "@mui/icons-material/DeleteForeverOutlined";
import { styled } from "@mui/material/styles";

const StyledRating = styled(Rating)({
  "& .MuiRating-iconFilled": {
    color: "#ff3d47",
  },
  "& .MuiRating-iconHover": {
    color: "#ff6d75",
  },
});

const MyShopItems = (props) => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);

  const [editShopsItem, setEditShopsItem] = useState(null);
  const [isUpdateBtn_item, setIsUpdateBtn_item] = useState(false);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/gk/item/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `JWT ${localStorage.getItem("access")}`,
        Accept: "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((resp) => setItems(resp))
      .catch((error) => console.log(error));
  }, [props.shop]);

  const editBtn = (shop) => {
    setEditShopsItem(shop);
    setIsUpdateBtn_item(true);
  };

  const updatedInformation = (shop) => {
    const new_shops = items.map((new_shop) => {
      if (new_shop.id === shop.id) {
        return shop;
      } else {
        return new_shop;
      }
    });

    setItems(new_shops);
    setIsUpdateBtn_item(false);
  };

  const itemForm = () => {
    setEditShopsItem({
      id: "",
      shopName: "",
      mobilePhone: "",
      email: "",
      address: "",
      city: "",
      ratings: "",
    });
  };

  const insertedInformation = (shop) => {
    const new_shop = [...items, shop];
    setItems(new_shop);
  };

  const deleteBtn_item = (shop) => {
    const new_shops = items.filter((my_shop) => {
      if (my_shop.id === shop.id) {
        return false;
      }
      return true;
    });

    setItems(new_shops);
  };

  const deleteBtn = (item) => {
    ApiService.DeleteMyShopItem(item.itemName)
      .then(() => deleteBtn_item(item))
      .catch((error) => console.log(error));
  };

  return (
    <>
      <Grid container spacing={3}>
        {items
          .filter((it) => it.shopID === props.shop.id)
          .map((row) => (
            <Grid item xs={6} key={row.id}>
              <Card className="text-center">
                <CardActionArea
                  onClick={() =>
                    navigate(`/shops/${props.shop.shopName}/${row.itemName}`)
                  }
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={row.itemImg}
                    alt={row.itemName}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {row.itemName}
                    </Typography>
                    <Typography gutterBottom>
                      {row.accumulatedRating ? (
                        <span className="fw-bold">
                          <StyledRating
                            value={row.accumulatedRating}
                            getLabelText={(value) =>
                              `${value} Heart${value !== 1 ? "s" : ""}`
                            }
                            precision={0.5}
                            icon={<FavoriteIcon fontSize="inherit" />}
                            emptyIcon={
                              <FavoriteBorderIcon fontSize="inherit" />
                            }
                            readOnly
                          />
                        </span>
                      ) : null}
                    </Typography>
                    <Typography variant="h6" color="text.primary">
                      {row.cost} BDT
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions className=" m-2 justify-content-center">
                  <Button
                    variant="contained"
                    size="large"
                    color="error"
                    startIcon={<UpdateOutlinedIcon />}
                    style={{
                      background: "#dc3545",
                    }}
                  >
                    Update
                  </Button>
                  <Button
                    variant="contained"
                    size="large"
                    color="error"
                    startIcon={<UpdateOutlinedIcon />}
                    style={{
                      background: "#dc3545",
                    }}
                  >
                    Update Image
                  </Button>
                  <Button
                    variant="contained"
                    size="large"
                    color="error"
                    // startIcon={<DeleteForeverOutlinedIcon />}
                    onClick={() => deleteBtn(row)}
                    style={{
                      background: "#dc3545",
                    }}
                  >
                    Delete
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
      </Grid>
      <Row>
        <Button
          variant="contained"
          size="large"
          color="error"
          startIcon={<DeleteForeverOutlinedIcon />}
          onClick={() => itemForm}
          style={{
            background: "#dc3545",
            width: "100%",
          }}
        >
          Insert Item
        </Button>
      </Row>
      <Row>
        {editShopsItem ? (
          <MyShopItemForm
            shop={editShopsItem}
            updatedInformation={updatedInformation}
            insertedInformation={insertedInformation}
            isUpdateBtn={isUpdateBtn_item}
          />
        ) : null}
      </Row>
    </>
  );
};

export default MyShopItems;
