import React, { useEffect, useState } from "react";
import ApiService from "../../ApiService";
import { Container, Row, Col, Figure, Form } from "react-bootstrap";

import {
  Button,
  CardActionArea,
  CardActions,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Avatar,
  styled,
  Rating,
} from "@mui/material";
import UpdateOutlinedIcon from "@mui/icons-material/UpdateOutlined";

export default function MyShopItemForm(props) {
  const [itemName, set_itemName] = useState(props.userInfo.itemName);
  const [cost, set_cost] = useState(props.userInfo.cost);
  const [itemDetail, set_itemDetail] = useState(props.userInfo.itemDetail);
  const [itemImg, set_itemImg] = useState(props.userInfo.itemImg);

  useEffect(() => {
    set_itemName(props.userInfo.itemName);
    set_cost(props.userInfo.cost);
    set_itemDetail(props.userInfo.itemDetail);
    set_itemImg(props.userInfo.itemImg);
  }, [props.userInfo]);

  const allNull = () => {
    set_itemName("");
    set_cost("");
    set_itemDetail("");
    set_itemImg("");
  };

  const [validated, setValidated] = useState(false);
  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  const onSubmit = (e) => {
    handleSubmit(e);
    e.preventDefault();

    if (props.isUpdateBtn_item) {
      ApiService.UpdateMyShopItem(
        props.shop.itemName,
        itemName,
        cost,
        itemDetail
      )
        .then((resp) => {
          props.updatedInformation(resp.data);
        })
        .then(allNull());
    } else {
      ApiService.InsertMyShopItem(
        props.shop.shopID,
        itemName,
        cost,
        itemDetail,
        itemImg
      )
        .then((resp) => props.insertedInformation(resp))
        .then(allNull());
    }
  };
  return (
    <Container fluid>
      {props.userInfo ? (
        <>
          <Row className="text-center">Update User Info Form</Row>
          <Row className="mb-4">
            <Form
              noValidate
              validated={validated}
              onSubmit={(e) => onSubmit(e)}
              // className="text-center mb-2"
            >
              <Row>
                <Form.Group as={Col} md="6">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="itemName"
                      value={itemName}
                      onChange={(e) => set_itemName(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">First Name</label>
                  </Form.Floating>
                </Form.Group>
                <Form.Group as={Col} md="6">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="cost"
                      value={cost}
                      onChange={(e) => set_cost(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Last Name</label>
                  </Form.Floating>
                </Form.Group>
              </Row>
              <Row>
                <Form.Group as={Col} md="4">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="itemDetail"
                      value={itemDetail}
                      onChange={(e) => set_itemDetail(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">House name</label>
                  </Form.Floating>
                </Form.Group>
                {!props.isUpdateBtn_item ? (
                  <Form.Group>
                    <Form.Floating className="mb-2">
                      <Form.Control
                        type="file"
                        // value={profile_pic}
                        onChange={(e) => set_itemImg(e.target.files[0])}
                        required
                      />
                    </Form.Floating>
                  </Form.Group>
                ) : null}
              </Row>

              {props.shop.id && props.isUpdateBtn_item ? (
                <Button
                  className=" rounded-3 text-white fw-bold fs-4"
                  variant="outlined"
                  size="large"
                  color="error"
                  startIcon={<UpdateOutlinedIcon />}
                  type="submit"
                  onClick={UpdateShops}
                  style={{
                    width: "100%",
                    background: "#dc3545",
                  }}
                >
                  Update
                </Button>
              ) : (
                <Button
                  className=" rounded-3 text-white fw-bold fs-4"
                  variant="outlined"
                  size="large"
                  color="error"
                  startIcon={<UpdateOutlinedIcon />}
                  type="submit"
                  onClick={InsertShops}
                  style={{
                    width: "100%",
                    background: "#dc3545",
                  }}
                >
                  Insert
                </Button>
              )}
            </Form>
          </Row>
        </>
      ) : null}
    </Container>
  );
}
