import { useState, useEffect } from "react";
import axios from "axios";
import { Container, Row, Col, Figure } from "react-bootstrap";
import NavBar from "./NavBar";

import { Link } from "react-router-dom";
import Animation from "./Animation";

import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import filterFactory, {
  textFilter,
  selectFilter,
} from "react-bootstrap-table2-filter";

export default function ShopList() {
  const [shop, setShop] = useState();
  const config = {
    headers: {
      "Content-Type": "multipart/form-data;application/json;",
      Authorization: `JWT ${localStorage.getItem("access")}`,
      Accept: "application/json",
    },
  };
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/gk/shop/`, config)
      .then((res) => setShop(res.data))
      .then((res) => console.table(res.data));
  });

  const { SearchBar, ClearSearchButton } = Search;

  const columns = [
    {
      dataField: "shopName",
      text: "Shop Name",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      formatter: (value) => {
        return (
          <Link to={`/shops/${value}`}>
            <div className="fs-3 text-decoration-underline text-white">
              {value}
            </div>
          </Link>
        );
      },
      sort: true,
      filter: textFilter(),
    },
    {
      dataField: "detailedAddr",
      text: "Address",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      formatter: (value) => {
        return <div className="fs-5">{value}</div>;
      },
      sort: true,
      filter: textFilter(),
    },
    {
      dataField: "review",
      text: "Ratings",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      formatter: (value) => {
        return <div className="fs-5">{value}</div>;
      },
      sort: true,
      filter: selectFilter({ options: selectOptions }),
    },
  ];

  const rowStyle = (row, rowIndex) => {
    const style = {};
    if (rowIndex % 2 == 0) {
      style.backgroundColor = "#c95f5f";
    } else {
      style.backgroundColor = "#bf3636";
    }
    style.textAlign = "center";
    style.color = "white";
    return style;
  };

  const pagination = paginationFactory({
    page: 1,
    sizePerPage: 10,
    lastPageText: ">>",
    firstPageText: "<<",
    nextPageText: ">",
    prePageText: "<",
    showTotal: true,
    alwaysShowAllBtns: false,
    onPageChange: function (page, sizePerPage) {
      console.log("page", page);
      console.log("sizePerPage", sizePerPage);
    },
    onSizePerPageChange: function (page, sizePerPage) {
      console.log("page", page);
      console.log("sizePerPage", sizePerPage);
    },
  });

  const defaultSorted = [
    {
      dataField: "ratings",
      order: "desc",
    },
  ];
  return (
    <Container fluid className="p-5">
      <Row>
        <br />
        <br />
        <br />
      </Row>
      <ToolkitProvider keyField="shopName" data={shop} columns={columns} search>
        {(props) => (
          <div>
            <Row>
              <Row>
                <Col sm={1} />
                <Col sm>
                  <div
                    className="shadow-lg p-4 rounded-3 text-center text-white text-wrap fs-1 fw-bold "
                    style={{
                      background: "#c95f5f",
                    }}
                  >
                    Search The Shops
                  </div>
                </Col>
                <Col sm={1} />
              </Row>
              <Row>
                <br />
              </Row>
              <Row
                className="shadow-lg p-4 rounded-3 text-center text-white "
                style={{
                  background: "#c95f5f",
                }}
              >
                <span>
                  <SearchBar
                    srText=""
                    style={{
                      textAlign: "center",
                      margin: "auto",
                      width: "100%",
                    }}
                    placeholder="Search About Shop"
                    {...props.searchProps}
                  />
                  <span> </span>
                  <ClearSearchButton
                    className="btn btn-secondary btn-sm"
                    style={{
                      width: "100%",
                    }}
                    {...props.searchProps}
                  />
                </span>
                <br />
                <br />
                <br />

                <BootstrapTable
                  keyField="shopName"
                  pagination={pagination}
                  filter={filterFactory()}
                  defaultSorted={defaultSorted}
                  bordered={false}
                  rowStyle={rowStyle}
                  {...props.baseProps}
                />
              </Row>
            </Row>
          </div>
        )}
      </ToolkitProvider>
    </Container>
  );
}
