import React, { useState, useEffect } from "react";
import { Row, Col } from "react-bootstrap";
import ApiService from "../ApiService";
import axios from "axios";

import Rating from "@mui/material/Rating";
import FavoriteIcon from "@mui/icons-material/Favorite";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import { styled } from "@mui/material/styles";

const StyledRating = styled(Rating)({
  "& .MuiRating-iconFilled": {
    color: "#ff3d47",
  },
  "& .MuiRating-iconHover": {
    color: "#ff6d75",
  },
});

export default function ItemReview(props, { isAuthenticated }) {
  const [itemReview, setItemReview] = useState([]);

  const [isUpdateComment, setIsUpdateComment] = useState(false);

  const [commentId, setCommentId] = useState();
  const [itemID] = useState(props.itemId);
  const [userID, setUserID] = useState([]);
  const [rating, setRating] = useState(null);
  const [comment, setComment] = useState("");

  console.table(userID);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/gk/review/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `JWT ${localStorage.getItem("access")}`,
        Accept: "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((resp) => setItemReview(resp))
      .catch((error) => console.log(error));
  }, [props.itemId]);

  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `JWT ${localStorage.getItem("access")}`,
      Accept: "application/json",
    },
  };

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/auth/users/me/`, config)
      .then((res) => setUserID(res.data))
      .catch((error) => console.log(error));
  }, [isAuthenticated]);

  const insertComment = () => {
    ApiService.InsertComment({
      userID,
      itemID,
      rating,
      comment,
    })
      .then((resp) => setItemReview(...itemReview, resp))
      .then(window.location.reload(false))
      .then(consol.log(userID, itemID, rating, comment))
      .then(setComment(""));
  };

  const deleteComment = (sr) => {
    const new_sr = itemReview.filter((my_sr) => {
      if (my_sr.id === sr.id) {
        return false;
      }
      return true;
    });

    setItemReview(new_sr);
  };

  const deleteBtn = (sr) => {
    ApiService.DeleteComment(sr.id)
      .then(() => deleteComment(sr))
      .then(consol.log(userID, itemID, rating, comment))
      .catch((error) => console.log(error));
  };

  const updateBtn = (sr) => {
    setIsUpdateComment(true);
    setComment(sr.comment);
    setCommentId(sr.id);
  };

  const updatedComment = (sr) => {
    const new_sr = itemReview.map((my_sr) => {
      if (my_sr.id === sr.id) {
        return sr;
      } else {
        return my_sr;
      }
    });

    setItemReview(new_sr);
  };

  const userCommentUpdate = () => {
    if (!comment) {
      return alert("Please write something to update the comment.");
    } else {
      return ApiService.UpdateComment(commentId, {
        userID,
        itemID,
        rating,
        comment,
      })
        .then((resp) => updatedComment(resp))
        .then(setIsUpdateComment(false))
        .then(consol.log(userID, itemID, rating, comment))
        .then(setComment(""))
        .catch((error) => console.log(error));
    }
  };

  return (
    <div>
      <Row>
        <Col sm={1}></Col>
        <Col
          sm={10}
          className="shadow-lg rounded-3 p-5"
          style={{
            background: "#c95f5f",
          }}
        >
          {itemReview
            .filter((sd) => sd.itemID === props.itemId)
            .map((sr) => {
              return (
                <div key={sr.id}>
                  <div>
                    <Row
                      className="shadow rounded-3 text-white p-3"
                      style={{
                        background: "#bf3636",
                      }}
                    >
                      <p>
                        {/* {sr.userId ? <UserInfo userId={sr.userId} /> : null} */}
                        <h3>{sr.comment}</h3>
                        <span>
                          <strong>Time: </strong> {sr.timeStampUpdated[11]}
                          {sr.timeStampUpdated[12]}
                          {sr.timeStampUpdated[13]}
                          {sr.timeStampUpdated[14]}
                          {sr.timeStampUpdated[15]} <strong> Date:</strong>{" "}
                          {sr.timeStampUpdated[8]}
                          {sr.timeStampUpdated[9]}
                          {sr.timeStampUpdated[7]}
                          {sr.timeStampUpdated[5]}
                          {sr.timeStampUpdated[6]}
                          {sr.timeStampUpdated[4]}
                          {sr.timeStampUpdated[0]}
                          {sr.timeStampUpdated[1]}
                          {sr.timeStampUpdated[2]}
                          {sr.timeStampUpdated[3]}
                        </span>
                        <br />
                      </p>

                      <br />
                      {sr.userID == userID.id ? (
                        <div>
                          <button
                            className="btn btn-primary mx-auto"
                            onClick={() => updateBtn(sr)}
                          >
                            Update
                          </button>{" "}
                          <button
                            onClick={() => deleteBtn(sr)}
                            className="btn btn-danger"
                          >
                            Delete
                          </button>
                        </div>
                      ) : null}
                    </Row>
                  </div>
                  <Row>
                    <br />
                    <br />
                  </Row>
                </div>
              );
            })}

          <div
            className="shadow rounded-3 text-white p-3"
            style={{
              background: "#bf3636",

              width: "100%",
            }}
          >
            <label className="pb-2 text-white fs-2">Comment </label>
            <textarea
              type="text"
              className="form-control"
              id="desc"
              rows="5"
              placeholder="Post Comment"
              style={{
                width: "100%",
              }}
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              required
            />
            <br />
            <StyledRating
              onChange={(e) => setRating(e.target.value)}
              value={rating}
              getLabelText={(value) =>
                `${value} Heart${value !== 1 ? "s" : ""}`
              }
              precision={0.5}
              icon={<FavoriteIcon fontSize="inherit" />}
              emptyIcon={<FavoriteBorderIcon fontSize="inherit" />}
            />
            <br />
            {isUpdateComment ? (
              <div class="col text-center text-white fw-2 pb-3">
                <button
                  type="button"
                  onClick={userCommentUpdate}
                  className="btn btn-lg text-white fs-4 "
                  style={{
                    background: "#c95f5f",
                  }}
                >
                  Update
                </button>
              </div>
            ) : (
              <div class="col text-center text-white fw-2 pb-3">
                <button
                  type="button"
                  onClick={insertComment}
                  className="btn btn-lg text-white fs-4 "
                  style={{
                    background: "#c95f5f",
                  }}
                >
                  Post
                </button>
              </div>
            )}
          </div>
        </Col>
        <Col sm={1}></Col>
      </Row>
    </div>
  );
}
