import React, { useState, useEffect } from "react";
import axios from "axios";

const useUserInfo = ({ isAuthenticated }) => {
  const [user, setUser] = useState([]);
  const [userDataEdit, setUserDataEdit] = useState([]);
  const [data, setData] = useState([]);

  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `JWT ${localStorage.getItem("access")}`,
      Accept: "application/json",
    },
  };

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/auth/users/me/`, config)
      .then((res) => setUser(res.data))
      .catch((error) => console.log(error));
  }, [isAuthenticated]);

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/accounts/users/`, config)
      .then((res) => setUserDataEdit(res.data))
      .catch((error) => console.log(error));
  }, []);

  data = userDataEdit.filter((userData) => userData.id === user.id);

  return data;
};
export default useUserInfo;
