import React, { Fragment, useState } from "react";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";
import { logout } from "../redux/actions/auth";
import { Navbar, Button, Nav, Figure } from "react-bootstrap";
import BrandTitle from "../assets/ghorKuno_title.png";
// import BrandTitle from "../assets/ghorKuno LOGO.png";

const NavBar = ({ logout, isAuthenticated }) => {
  const [redirect, setRedirect] = useState(false);

  const logout_user = () => {
    logout();
    setRedirect(true);
  };

  const navLinkStyle = ({ isActive }) => {
    return {
      textDecoration: isActive ? "underline" : "none",
    };
  };

  const guestNavLinks = () => (
    <Fragment>
      <Nav.Item>
        <NavLink style={navLinkStyle} className="px-2" to="/login">
          Login
        </NavLink>
      </Nav.Item>
      <Nav.Item>
        <NavLink style={navLinkStyle} className="px-2" to="/signup">
          Sign Up
        </NavLink>
      </Nav.Item>
    </Fragment>
  );

  const authNavLinks = () => (
    <Fragment>
      <Nav.Item>
        <NavLink style={navLinkStyle} className="px-2" to="/user-info">
          UserInfo
        </NavLink>
      </Nav.Item>
      <Navbar.Collapse
        className="justify-content-end"
        // id="responsive-navbar-nav"
      >
        <Nav className="">
          <Nav.Item>
            <Button
              variant="warning"
              onClick={logout_user}
              as={NavLink}
              to="/login"
            >
              Logout
            </Button>
          </Nav.Item>
        </Nav>
      </Navbar.Collapse>
    </Fragment>
  );
  {
    !isAuthenticated ? guestNavLinks() : authNavLinks();
  }
  return (
    <Navbar bg="light" variant={"light"} expand="lg">
      <Navbar.Brand className="px-5" href="/home">
        <img
          width={200}
          height={30}
          alt="GhorKuno"
          src={BrandTitle}
          className="d-inline-block align-top"
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav variant="pills" className="fs-5 fw-bold ">
          <Nav.Item>
            <NavLink to="/home" style={navLinkStyle} className="px-2">
              Home
            </NavLink>
          </Nav.Item>
          {!isAuthenticated ? guestNavLinks() : authNavLinks()}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { logout })(NavBar);
