import React, { useState, useEffect } from "react";
import { Container, Row, Col, Figure } from "react-bootstrap";

import { Link } from "react-router-dom";

import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import "../../node_modules/react-bootstrap-table2-toolkit/dist/react-bootstrap-table2-toolkit.min.css";
import "../../node_modules/react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";

import BootstrapTable from "react-bootstrap-table-next";
import "../../node_modules/react-bootstrap-table2-filter/dist/react-bootstrap-table2-filter.min.css";

import paginationFactory from "react-bootstrap-table2-paginator";
import "../../node_modules/react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css";

import filterFactory, { textFilter } from "react-bootstrap-table2-filter";

function ShopList() {
  const [shopList, setShopList] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/gk/shop/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `JWT ${localStorage.getItem("access")}`,
        Accept: "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((result) => setShopList(result))
      .catch((error) => console.log(error));
  }, []);

  const { SearchBar, ClearSearchButton } = Search;

  const columns = [
    {
      dataField: "shopName",
      text: "Shop Name",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      formatter: (value) => {
        return (
          <Link to={`/shops/${value}`}>
            <div className="fs-3 text-decoration-underline text-white">
              {value}
            </div>
          </Link>
        );
      },
      sort: true,
      filter: textFilter(),
    },
    {
      dataField: "detailedAddr",
      text: "Address",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      formatter: (value) => {
        return <div className="fs-5">{value}</div>;
      },
      sort: true,
      filter: textFilter(),
    },
    {
      dataField: "review",
      text: "Ratings",
      headerStyle: () => {
        return {
          backgroundColor: "#bf3636",
          textAlign: "center",
          srText: "",
          color: "#ede8e8",
        };
      },
      formatter: (value) => {
        return <div className="fs-5">{value}</div>;
      },
      sort: true,
    },
  ];

  const rowStyle = (row, rowIndex) => {
    const style = {};
    if (rowIndex % 2 == 0) {
      style.backgroundColor = "#c95f5f";
    } else {
      style.backgroundColor = "#bf3636";
    }
    style.textAlign = "center";
    style.color = "white";
    return style;
  };

  const pagination = paginationFactory({
    page: 1,
    sizePerPage: 10,
    lastPageText: ">>",
    firstPageText: "<<",
    nextPageText: ">",
    prePageText: "<",
    showTotal: true,
    alwaysShowAllBtns: false,
    onPageChange: function (page, sizePerPage) {
      console.log("page", page);
      console.log("sizePerPage", sizePerPage);
    },
    onSizePerPageChange: function (page, sizePerPage) {
      console.log("page", page);
      console.log("sizePerPage", sizePerPage);
    },
  });

  const defaultSorted = [
    {
      dataField: "ratings",
      order: "desc",
    },
  ];

  return (
    <Container fluid className="p-5">
      <Row>
        <br />
        <br />
        <br />
      </Row>
      <ToolkitProvider
        keyField="shopName"
        data={shopList}
        columns={columns}
        search
      >
        {(props) => (
          <div>
            <Row>
              <Row>
                <Col sm={1} />
                <Col sm>
                  <div
                    className="shadow-lg p-4 rounded-3 text-center text-white text-wrap fs-1 fw-bold "
                    style={{
                      background: "#c95f5f",
                    }}
                  >
                    Search The Shops
                  </div>
                </Col>
                <Col sm={1} />
              </Row>
              <Row>
                <br />
              </Row>
              <Row
                className="shadow-lg p-4 rounded-3 text-center text-white "
                style={{
                  background: "#c95f5f",
                }}
              >
                <span>
                  <SearchBar
                    srText=""
                    style={{
                      textAlign: "center",
                      margin: "auto",
                      width: "100%",
                    }}
                    placeholder="Search About Shop"
                    {...props.searchProps}
                  />
                  <span> </span>
                  <ClearSearchButton
                    className="btn btn-secondary btn-sm"
                    style={{
                      width: "100%",
                    }}
                    {...props.searchProps}
                  />
                </span>
                <br />
                <br />
                <br />

                <BootstrapTable
                  keyField="shopName"
                  pagination={pagination}
                  filter={filterFactory()}
                  defaultSorted={defaultSorted}
                  bordered={false}
                  rowStyle={rowStyle}
                  {...props.baseProps}
                />
              </Row>
            </Row>
          </div>
        )}
      </ToolkitProvider>
    </Container>
  );
}

export default ShopList;
