import React, { useState } from "react";
import { Link, Navigate } from "react-router-dom";
import { connect } from "react-redux";
import { login } from "../../redux/actions/auth";

import { Form, Container, Row, Col, Button } from "react-bootstrap";
import { ReactComponent as kitchen_mom } from "../../assets/kitchen_mom.svg";

import grorKuno_Combained from "../../assets/grorKuno_Combained.png";

const Login = ({ login, isAuthenticated, isSignUp }) => {
  const [validated, setValidated] = useState(false);

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  const onSubmit = (e) => {
    handleSubmit(e);
    e.preventDefault();
    login(email, password);
  };

  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const { email, password } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  if (isAuthenticated) {
    return <Navigate to="/" />;
  }

  return (
    <Container fluid>
      <Row
        className="d-flex align-items-center justify-content-center"
        style={{
          position: "absolute",
          top: "30%",
          width: "30%",
        }}
      >
        <Row>
          <grorKuno_Combained />
        </Row>
        <Row>
          <Form
            noValidate
            validated={validated}
            onSubmit={(e) => onSubmit(e)}
            className="text-center mb-3"
          >
            <Form.Group>
              <Form.Floating className="mb-3">
                <Form.Control
                  className="shadow-lg rounded-3"
                  type="email"
                  placeholder="name@example.com"
                  name="email"
                  value={email}
                  onChange={(e) => onChange(e)}
                  required
                />
                <label htmlFor="floatingInputCustom">Email address</label>
              </Form.Floating>
            </Form.Group>
            <Form.Group>
              <Form.Floating className="mb-3">
                <Form.Control
                  className="shadow-lg rounded-3"
                  type="password"
                  placeholder="Password"
                  name="password"
                  value={password}
                  onChange={(e) => onChange(e)}
                  required
                />
                <label htmlFor="floatingPasswordCustom">Password</label>
              </Form.Floating>
            </Form.Group>
            <Button
              variant="danger"
              type="submit"
              className="shadow-lg rounded-3 text-white fw-bold fs-4"
              style={{
                height: "30%",
                width: "100%",
              }}
            >
              Login
            </Button>
          </Form>
        </Row>
        <Row>
          <br />
        </Row>
        <Col sm={6}>
          <Link to="/signup" className="text-decoration-none">
            <Button
              variant="warning"
              type="submit"
              className="shadow-lg rounded-3 text-black fw-bold fs-5"
              style={{
                width: "100%",
              }}
            >
              New here? Sign up
            </Button>
          </Link>
        </Col>
        <Col sm={6}>
          <Link to="/reset-password">
            <Button
              variant="danger"
              type="submit"
              className="shadow-lg rounded-3 text-white fw-bold fs-5"
              style={{
                width: "100%",
              }}
            >
              Reset Password
            </Button>
          </Link>
        </Col>
      </Row>
    </Container>
  );
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { login })(Login);
