import React, { useEffect, useState } from "react";
import ApiService from "../../ApiService";
import { Container, Row, Col, Figure, Form, Button } from "react-bootstrap";

export default function UserInfoForm(props) {
  const [first_name, set_first_name] = useState(props.userInfo.first_name);
  const [last_name, set_last_name] = useState(props.userInfo.last_name);
  const [house_name, set_house_name] = useState(props.userInfo.house_name);
  const [road_no, set_road_no] = useState(props.userInfo.road_no);
  const [block_no, set_block_no] = useState(props.userInfo.block_no);
  const [area, set_area] = useState(props.userInfo.area);
  const [city, set_city] = useState(props.userInfo.city);
  const [district, set_district] = useState(props.userInfo.district);
  const [mobilePhone, set_mobilePhone] = useState(props.userInfo.mobilePhone);
  const [email, set_email] = useState(props.userInfo.email);
  const [password, set_password] = useState("");

  const allNull = () => {
    set_first_name("");
    set_last_name("");
    set_house_name("");
    set_road_no("");
    set_block_no("");
    set_area("");
    set_city("");
    set_district("");
    set_mobilePhone("");
    set_email("");
  };

  useEffect(() => {
    set_first_name(props.userInfo.first_name);
    set_last_name(props.userInfo.last_name);
    set_house_name(props.userInfo.house_name);
    set_road_no(props.userInfo.road_no);
    set_block_no(props.userInfo.block_no);
    set_area(props.userInfo.area);
    set_city(props.userInfo.city);
    set_district(props.userInfo.district);
    set_mobilePhone(props.userInfo.mobilePhone);
    set_email(props.userInfo.email);
  }, [props.userInfo]);

  const UpdateUserInfo = () => {
    ApiService.UpdateUserInfo(props.userInfo.id, {
      first_name,
      last_name,
      house_name,
      road_no,
      block_no,
      area,
      city,
      district,
      mobilePhone,
      email,
      password,
    })
      .then((resp) => props.updatedInformation(resp))
      .then(allNull());
  };

  const [validated, setValidated] = useState(false);
  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  const onSubmit = (e) => {
    handleSubmit(e);
    e.preventDefault();
    ApiService.UpdateUserInfo(props.userInfo.id, {
      first_name,
      last_name,
      house_name,
      road_no,
      block_no,
      area,
      city,
      district,
      mobilePhone,
      email,
      password,
    }).then((resp) => props.updatedInformation(resp));
    // else return alert("Password's doesn't match!!");
  };
  return (
    <Container fluid>
      {props.userInfo ? (
        <>
          <Row className="text-center">Update User Info Form</Row>
          <Row className="mb-4">
            <Form
              noValidate
              validated={validated}
              onSubmit={(e) => onSubmit(e)}
              // className="text-center mb-2"
            >
              <Row>
                <Form.Group as={Col} md="6">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="first_name"
                      value={first_name}
                      onChange={(e) => set_first_name(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">First Name</label>
                  </Form.Floating>
                </Form.Group>
                <Form.Group as={Col} md="6">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="last_name"
                      value={last_name}
                      onChange={(e) => set_last_name(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Last Name</label>
                  </Form.Floating>
                </Form.Group>
              </Row>
              <Row>
                <Form.Group as={Col} md="4">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="house_name"
                      value={house_name}
                      onChange={(e) => set_house_name(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">House name</label>
                  </Form.Floating>
                </Form.Group>
                <Form.Group as={Col} md="4">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="road_no"
                      value={road_no}
                      onChange={(e) => set_road_no(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Road no</label>
                  </Form.Floating>
                </Form.Group>
                <Form.Group as={Col} md="4">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="block_no"
                      value={block_no}
                      onChange={(e) => set_block_no(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Block no</label>
                  </Form.Floating>
                </Form.Group>
              </Row>
              <Row>
                <Form.Group as={Col} md="4">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="area"
                      value={area}
                      onChange={(e) => set_area(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Area</label>
                  </Form.Floating>
                </Form.Group>
                <Form.Group as={Col} md="4">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="city"
                      value={city}
                      onChange={(e) => set_city(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">City</label>
                  </Form.Floating>
                </Form.Group>
                <Form.Group as={Col} md="4">
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      placeholder="name"
                      name="district"
                      value={district}
                      onChange={(e) => set_district(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">District</label>
                  </Form.Floating>
                </Form.Group>
              </Row>
              <Form.Group>
                <Form.Floating className="mb-2">
                  <Form.Control
                    className=" rounded-3"
                    type="text"
                    placeholder="name"
                    name="mobilePhone"
                    value={mobilePhone}
                    onChange={(e) => set_mobilePhone(e.target.value)}
                    required
                  />
                  <label htmlFor="floatingInputCustom">Mobile Phone</label>
                </Form.Floating>
              </Form.Group>
              <Form.Group>
                <Form.Floating className="mb-2">
                  <Form.Control
                    className=" rounded-3"
                    type="password"
                    placeholder="Password"
                    name="password"
                    value={password}
                    onChange={(e) => set_password(e.target.value)}
                    required
                  />
                  <label htmlFor="floatingPasswordCustom">
                    Input your Password
                  </label>
                </Form.Floating>
              </Form.Group>

              <Button
                variant="danger"
                type="submit"
                className=" rounded-3 text-white fw-bold fs-4"
                style={{
                  width: "100%",
                }}
              >
                Update
              </Button>
            </Form>
          </Row>
        </>
      ) : null}
    </Container>
  );
}
