import { Container, Row, Col, Figure, Button } from "react-bootstrap";
import { Link, Navigate, useLocation, useNavigate } from "react-router-dom";

export default function UserInfo(props) {
  const navigate = useNavigate();
  const reset_password_btn_click = () => {
    navigate("/reset-password");
  };
  const editBtn = (user) => {
    props.editBtn(user);
  };

  const editBtn_ProfilePic = (user) => {
    props.editBtn_ProfilePic(user);
  };

  return (
    <div>
      <br />
      <Container>
        <Row>
          <Col sm>
            Name: {props.userInfo.first_name} {props.userInfo.last_name}
            <br />
            {props.userInfo.profile_pic ? (
              <>
                profile_pic
                <Figure className="text-center p-2">
                  <Figure.Image
                    width={300}
                    height={200}
                    alt="none"
                    src={props.userInfo.profile_pic}
                  />
                </Figure>
              </>
            ) : (
              <>Please upload a profile pic</>
            )}
          </Col>
        </Row>
        <Row>
          <Col sm>
            <button
              className="btn btn-danger p-2"
              onClick={() => editBtn(props.userInfo)}
            >
              Want to update your info?
            </button>
          </Col>
          <Col sm>
            <button
              className="btn btn-danger p-2"
              onClick={() => editBtn_ProfilePic(props.userInfo)}
            >
              Want to update your Profile Pic?
            </button>
          </Col>
          <Col sm>
            <Button onClick={reset_password_btn_click}>
              Reset your password here
            </Button>
          </Col>
        </Row>
      </Container>
    </div>
  );
}
