import React, { useState } from "react";
import { Link, Navigate } from "react-router-dom";
import { connect } from "react-redux";
import { login } from "../../redux/actions/auth";

import { Form, Container, Row, Col, Button } from "react-bootstrap";

const Login = ({ login, isAuthenticated, isSignUp }) => {
  const [validated, setValidated] = useState(false);

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
      login(email, password);
    }

    setValidated(true);
  };

  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const { email, password } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    login(email, password);
  };

  if (isAuthenticated) {
    return <Navigate to="/" />;
  }

  return (
    <Container
      fluid
      className="border d-flex align-items-center justify-content-center"
    >
      <Row
        style={{
          position: "absolute",
          top: "30%",
          width: "30%",
        }}
      >
        <Form
          noValidate
          validated={validated}
          onSubmit={handleSubmit}
          className="text-center mb-3"
        >
          <Form.Group>
            <Form.Floating className="mb-3">
              <Form.Control
                className="shadow-lg rounded-3"
                id="floatingInputCustom"
                type="email"
                placeholder="name@example.com"
                onChange={(e) => onChange(e)}
                required
              />
              <label htmlFor="floatingInputCustom">Email address</label>
            </Form.Floating>
          </Form.Group>
          <Form.Group>
            <Form.Floating className="mb-3">
              <Form.Control
                className="shadow-lg rounded-3"
                id="floatingPasswordCustom"
                type="password"
                placeholder="Password"
                onChange={(e) => onChange(e)}
                required
              />
              <label htmlFor="floatingPasswordCustom">Password</label>
            </Form.Floating>
          </Form.Group>
          <Button
            variant="danger"
            type="submit"
            className="shadow-lg rounded-3 text-white fw-bold fs-4"
            style={{
              height: "30%",
              width: "100%",
            }}
          >
            Login
          </Button>
        </Form>
        <Row>
          <br />
        </Row>
        <Col sm={6}>
          <Link to="/signup" className="text-decoration-none">
            <Button
              variant="warning"
              type="submit"
              className="shadow-lg rounded-3 text-black fw-bold fs-5"
              style={{
                width: "100%",
              }}
            >
              New here? Sign up
            </Button>
          </Link>
        </Col>
        <Col sm={6}>
          <Link to="/reset-password">
            <Button
              variant="danger"
              type="submit"
              className="shadow-lg rounded-3 text-white fw-bold fs-5"
              style={{
                width: "100%",
              }}
            >
              Reset Password
            </Button>
          </Link>
        </Col>
      </Row>
    </Container>
    // <div className="container mt-5">
    //   <div>
    //     <h1>Login</h1>
    //     <p>Login into your Account</p>
    //     <form onSubmit={(e) => onSubmit(e)}>
    //       <div className="form-group">
    //         <input
    //           className="form-control"
    //           type="email"
    //           placeholder="Email"
    //           name="email"
    //           value={email}
    //           onChange={(e) => onChange(e)}
    //           required
    //         />
    //       </div>
    //       <div className="form-group">
    //         <input
    //           className="form-control"
    //           type="password"
    //           placeholder="Password"
    //           name="password"
    //           value={password}
    //           onChange={(e) => onChange(e)}
    //           minLength="6"
    //           required
    //         />
    //       </div>
    //       <button className="btn btn-primary" type="submit">
    //         Login
    //       </button>
    //     </form>
    //     {/* <button className='btn btn-danger mt-3' onClick={continueWithGoogle}>
    //             Continue With Google
    //         </button>
    //         <br />
    //         <button className='btn btn-primary mt-3' onClick={continueWithFacebook}>
    //             Continue With Facebook
    //         </button> */}
    //     <p className="mt-3">
    //       Don't have an account? <Link to="/signup">Sign Up</Link>
    //     </p>
    //     <p className="mt-3">
    //       Forgot your Password? <Link to="/reset-password">Reset Password</Link>
    //     </p>
    //   </div>
    // </div>
  );
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { login })(Login);
