import React, { useState, useEffect } from "react";
import { Link, Navigate, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import ResetPassword from "../../containers/LogInAndSignUp/ResetPassword";
import { Form, Container, Row, Col, Button, Figure } from "react-bootstrap";
import useFetchGet from "../../hocs/useFetchGet";

import UserInfo from "./UserInfo";
import UserInfoForm from "./UserInfoForm";

const UserInfoIndex = ({ isAuthenticated }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState([]);
  const [userDataEdit, setUserDataEdit] = useState([]);

  const [editUserInfo, setEditUserInfo] = useState(null);
  const [isUpdateBtn, setIsUpdateBtn] = useState(false);

  // const [first_name, set_first_name] = useState("");
  // const [last_name, set_last_name] = useState("");
  // const [house_name , set_house_name ] = useState("");
  // const [ road_no,set_road_no ] = useState("");
  // const [ block_no,set_block_no ] = useState("");
  // const [ area,set_area ] = useState("");
  // const [ city,set_city ] = useState("");
  // const [ district,set_district ] = useState("");
  // const [ mobilePhone,set_mobilePhone ] = useState("");

  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    house_name: "",
    road_no: "",
    block_no: "",
    area: "",
    city: "",
    district: "",
    mobilePhone: "",
  });

  const {
    first_name,
    last_name,
    house_name,
    road_no,
    block_no,
    area,
    city,
    district,
    mobilePhone,
  } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `JWT ${localStorage.getItem("access")}`,
      Accept: "application/json",
    },
  };

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/auth/users/me/`, config)
      .then((res) => setUser(res.data))
      .catch((error) => console.log(error));
  }, [isAuthenticated]);

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/accounts/users/`, config)
      .then((res) => setUserDataEdit(res.data))
      .catch((error) => console.log(error));
  }, []);

  const reset_password_btn_click = () => {
    navigate("/reset-password");
  };

  const [userData] = userDataEdit.filter((userData) => userData.id === user.id);
  console.table(userData);

  const editBtn = (shop) => {
    setEditUserInfo(shop);
    setIsUpdateBtn(true);
  };

  const updatedInformation = (user) => {
    const new_userInfo = user.map((new_) => {
      if (new_.id === user.id) {
        return user;
      } else {
        return new_;
      }
    });
    setUser(new_userInfo);
    setIsUpdateBtn(false);
  };

  const userInfoForm = () => {
    setEditUserInfo({
      first_name: "",
      last_name: "",
      house_name: "",
      road_no: "",
      block_no: "",
      area: "",
      city: "",
      district: "",
      mobilePhone: "",
    });
  };

  return (
    <div className="text-center">
      {userData ? (
        <UserInfo
          userInfo={userData}
          editBtn={editBtn}
          shopOfferForm={ShopOfferForm}
        />
      ) : null}
      <br /> <br />
      <Button onClick={reset_password_btn_click}>
        Reset your password here
      </Button>
      <br /> <br />
      {isUpdateBtn ? (
        <UserInfoForm
          userInfo={userDataEdit}
          updatedInformation={updatedInformation}
          isUpdateBtn={isUpdateBtn}
        />
      ) : null}
    </div>
  );
};

export default UserInfoIndex;
