import { useState, useEffect } from "react";

import purple from "@material-ui/core/colors/purple";
import Grid from "@material-ui/core/Grid";

import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { Button, CardActionArea, CardActions } from "@mui/material";

const ShopItems = (props) => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/gk/item/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `JWT ${localStorage.getItem("access")}`,
        Accept: "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((resp) => setItems(resp))
      .catch((error) => console.log(error));
  }, [props.shopId]);

  items
    .filter((it) => it.shopId === props.shopId)
    .map((item) => console.log(item.itemName));

  return (
    <Grid container spacing={2}>
      {items.map((row) => (
        <Grid item xs={4}>
          <Card className={"MuiElevatedCard--01"}>
            <CardActionArea>
              <CardMedia
                component="img"
                height="140"
                image={row.itemImg}
                alt={row.itemName}
              />
              <CardContent className={"MuiCardContent-root"}>
                <Typography gutterBottom variant="h5" component="div">
                  {row.itemName}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {row.itemDetail}
                </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions>
              <Button size="small" color="primary">
                Share
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

ShopItems.getTheme = (muiBaseTheme) => {
  const offset = 40;
  const cardShadow = "0px 14px 80px rgba(34, 35, 58, 0.2)";
  const headerShadow = "4px 4px 20px 1px rgba(0, 0, 0, 0.2)";
  return {
    MuiCard: {
      root: {
        "&.MuiElevatedCard--01": {
          marginTop: 50,
          borderRadius: muiBaseTheme.spacing.unit / 2,
          transition: "0.3s",
          boxShadow: cardShadow,
          position: "relative",
          width: "100%",
          overflow: "initial",
          background: "#ffffff",
          padding: `${muiBaseTheme.spacing.unit * 6}px 0`,
          "& .MuiCardHeader-root": {
            flexShrink: 0,
            position: "absolute",
            top: -offset,
            right: 20,
            left: 20,
            borderRadius: muiBaseTheme.spacing.unit / 2,
            backgroundColor: purple[600],
            overflow: "hidden",
            boxShadow: headerShadow,
            textAlign: "center",
            "& .MuiCardHeader-title": {
              color: "#ffffff",
              fontWeight: 900,
              letterSpacing: 1,
            },
            "& .MuiCardHeader-subheader": {
              color: "#ffffff",
              opacity: 0.87,
              fontWeight: 200,
              letterSpacing: 0.4,
            },
          },
          "& .MuiDivider-root": {
            marginLeft: muiBaseTheme.spacing.unit * 5,
            marginRight: muiBaseTheme.spacing.unit * 5,
            paddingTop: "5px",
            marginTop: 5,
            marginBottom: 5,
          },
          "& .MuiLabel-root": {
            paddingTop: "5px",
          },

          "& .MuiCardContent-root": {
            textAlign: "left",

            "& .MuiCardContent-inner": {
              paddingTop: "38px",
              overflowX: "auto",
            },
          },
        },
      },
    },
  };
};

export default ShopItems;
