import React, { useState, useEffect } from "react";
import { Link, Navigate, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import ResetPassword from "../containers/LogInAndSignUp/ResetPassword";
import { Form, Container, Row, Col, Button, Figure } from "react-bootstrap";
import useUserInfo from "../hocs/useUserInfo";

const UserInfo = ({ isAuthenticated }) => {
  const [userData] = useUserInfo(isAuthenticated);
  const navigate = useNavigate();

  const reset_password_btn_click = () => {
    navigate("/reset-password");
  };

  return (
    <div className="text-center">
      <>
        {userData.is_worker ? (
          <h1>User is worker</h1>
        ) : (
          <h1>User is not worker</h1>
        )}
        <br /> <br /> <br />
        <h1>First name: {userData.first_name}</h1>
        <h1>Last name: {userData.last_name}</h1>
        <br /> <br />
        {userData.is_staff ? (
          <h1>User is staff</h1>
        ) : (
          <h1>User is not staff</h1>
        )}
      </>
      <br /> <br />
      <h1>Last login:{userData.last_login}</h1>
      <Button onClick={reset_password_btn_click}>
        Reset your password here
      </Button>
    </div>
  );
};

export default UserInfo;
