import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Container, Row, Col, Figure, ProgressBar } from "react-bootstrap";
import Rating from "@mui/material/Rating";
import FavoriteIcon from "@mui/icons-material/Favorite";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import { styled } from "@mui/material/styles";

const StyledRating = styled(Rating)({
  "& .MuiRating-iconFilled": {
    color: "#ff3d47",
  },
  "& .MuiRating-iconHover": {
    color: "#ff6d75",
  },
});

export default function IndividualItem() {
  const [item, setItem] = useState([]);
  const { itemName } = useParams();

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/gk/item/${itemName}/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `JWT ${localStorage.getItem("access")}`,
        Accept: "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((resp) => setItem(resp))
      .catch((error) => console.log(error));
  }, [shopName]);

  return (
    <Container fluid="sm">
      <Row>
        <Col
          sm
          className="shadow-lg p-4 rounded-3 text-center text-white text-wrap fs-1 fw-bold "
          style={{
            background: "#FFBD2D",
          }}
        >
          {item.itemName}
        </Col>
      </Row>
      <br />
      <Row>
        <Grid item xs={4} key={row.id}>
          <Card>
            <CardMedia
              component="img"
              height="200"
              image={item.itemImg}
              alt={item.itemName}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {item.itemName}
              </Typography>
              <Typography gutterBottom variant="h5" component="div">
                {item.accumulatedRating ? (
                  <span className="fw-bold">
                    <StyledRating
                      value={item.accumulatedRating}
                      getLabelText={(value) =>
                        `${value} Heart${value !== 1 ? "s" : ""}`
                      }
                      precision={0.5}
                      icon={<FavoriteIcon fontSize="inherit" />}
                      emptyIcon={<FavoriteBorderIcon fontSize="inherit" />}
                      readOnly
                    />
                  </span>
                ) : null}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {item.itemDetail}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" color="primary">
                Share
              </Button>
            </CardActions>
          </Card>
        </Grid>
      </Row>
      <Row>
        <Col
          sm
          className="shadow-lg p-4 rounded-3 text-center text-white text-wrap fs-1 fw-bold "
          style={{
            background: "#FFBD2D",
          }}
        >
          Shop Items
        </Col>
      </Row>
      <Row>
        <br />
      </Row>
      <Row className="p-2"></Row>
    </Container>
  );
}
