import React, { useEffect, useState } from "react";
import ApiService from "../../ApiService";
import { Container, Row, Col, Form } from "react-bootstrap";

import { Button } from "@mui/material";
import UpdateOutlinedIcon from "@mui/icons-material/UpdateOutlined";

export default function MyShopForm(props) {
  const [shopName, set_shopName] = useState(props.shopInfo.shopName);
  const [detailedAddr, set_detailedAddr] = useState(
    props.shopInfo.detailedAddr
  );

  useEffect(() => {
    set_shopName(props.shopInfo.shopName);
    set_detailedAddr(props.shopInfo.detailedAddr);
  }, [props.shopInfo]);

  const [validated, setValidated] = useState(false);
  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  const onSubmit = (e) => {
    handleSubmit(e);
    e.preventDefault();
    ApiService.UpdateShopInfo(
      props.shopInfo.shopName,
      shopName,
      detailedAddr
    ).then((resp) => {
      props.updatedInformation(resp.data);
    });
  };
  return (
    <Container fluid>
      {props.shopInfo ? (
        <>
          <Row className="text-center">Update Shop Info</Row>
          <Row className="mb-4">
            <Form
              noValidate
              validated={validated}
              onSubmit={(e) => onSubmit(e)}
              // className="text-center mb-2"
            >
              <Row>
                <Form.Group>
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      name="shopName"
                      value={shopName}
                      onChange={(e) => set_shopName(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Shop Name</label>
                  </Form.Floating>
                </Form.Group>
              </Row>
              <Row>
                <Form.Group>
                  <Form.Floating className="mb-2">
                    <Form.Control
                      className=" rounded-3"
                      type="text"
                      name="detailedAddr"
                      value={detailedAddr}
                      onChange={(e) => set_detailedAddr(e.target.value)}
                      required
                    />
                    <label htmlFor="floatingInputCustom">Address</label>
                  </Form.Floating>
                </Form.Group>
              </Row>
              <Button
                variant="outlined"
                size="large"
                color="error"
                startIcon={<UpdateOutlinedIcon />}
                type="submit"
                sx={{
                  width: "100%",
                  background: "#dc3545",
                }}
              >
                Update
              </Button>
            </Form>
          </Row>
        </>
      ) : null}
    </Container>
  );
}
