import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = ({ user }) => {
  // const [user, setUser] = useState([]);

  // const config = {
  //   headers: {
  //     "Content-Type": "application/json",
  //     Authorization: `JWT ${localStorage.getItem("access")}`,
  //     Accept: "application/json",
  //   },
  // };

  // useEffect(async () => {
  //   const res = await axios.get(
  //     `${process.env.REACT_APP_API_URL}/auth/users/me/`,
  //     config
  //   );
  //   setUser(res.data);
  // }, [user]);

  return (
    <div className="container text-center">
      <div className="jumbotron mt-5">
        <h1 className="display-4">
          Welcome to <span className="fw-bold">GhorKuno</span>
        </h1>
        <p className="lead">
          This is an system with production level features!
        </p>
        <hr className="my-4" />
        <h1>First name:{user.first_name}</h1>
        <h1>Last name:{user.last_name}</h1>

        {user.is_staff ? <h1>User is staff</h1> : <h1>User is not staff</h1>}
        {user.is_worker ? <h1>User is worker</h1> : <h1>User is not worker</h1>}

        <h1>Last login:{user.last_login}</h1>
      </div>
    </div>
  );
};

export default Home;
