import React from "react";
import ApiService from "../../ApiService";
import { Container, Row, Col, Figure } from "react-bootstrap";

function MyShopItemList(props) {
  const editBtn = (shop) => {
    props.editBtn(shop);
  };

  const deleteBtn = (item) => {
    ApiService.DeleteMyShopItem(item.itemName)
      .then(() => props.deleteBtn(item))
      .catch((error) => console.log(error));
  };

  const shopForm = () => {
    props.itemForm();
  };

  return (
    <div>
      <br />
      <Grid container spacing={3}>
        {props.items
          .filter((it) => it.shopID === props.shop.id)
          .map((row) => (
            <Grid item xs={6} key={row.id}>
              <Card className="text-center">
                <CardActionArea
                  onClick={() =>
                    navigate(`/shops/${props.shop.shopName}/${row.itemName}`)
                  }
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={row.itemImg}
                    alt={row.itemName}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {row.itemName}
                    </Typography>
                    <Typography gutterBottom>
                      {row.accumulatedRating ? (
                        <span className="fw-bold">
                          <StyledRating
                            value={row.accumulatedRating}
                            getLabelText={(value) =>
                              `${value} Heart${value !== 1 ? "s" : ""}`
                            }
                            precision={0.5}
                            icon={<FavoriteIcon fontSize="inherit" />}
                            emptyIcon={
                              <FavoriteBorderIcon fontSize="inherit" />
                            }
                            readOnly
                          />
                        </span>
                      ) : null}
                    </Typography>
                    <Typography variant="h6" color="text.primary">
                      {row.cost} BDT
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions className=" m-2 justify-content-center">
                  <Button
                    onClick={() => editBtn(row)}
                    variant="contained"
                    size="large"
                    color="error"
                    startIcon={<UpdateOutlinedIcon />}
                    style={{
                      background: "#dc3545",
                    }}
                  >
                    Update
                  </Button>
                  <Button
                    //   onClick={() => deleteBtn(row)}
                    variant="contained"
                    size="large"
                    color="error"
                    startIcon={<UpdateOutlinedIcon />}
                    style={{
                      background: "#dc3545",
                    }}
                  >
                    Update Image
                  </Button>
                  <Button
                    variant="contained"
                    size="large"
                    color="error"
                    startIcon={<DeleteForeverOutlinedIcon />}
                    onClick={() => deleteBtn(row)}
                    style={{
                      background: "#dc3545",
                    }}
                  >
                    Delete
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
      </Grid>
      <Row>
        <Col sm={2}></Col>
        <Col
          sm
          className="p-3 rounded-3 text-center text-white text-wrap fs-1 fw-bold "
        >
          <button
            onClick={shopForm}
            className="btn btn-primary fs-1 text-white pb-2"
          >
            Insert Shop
          </button>
        </Col>
        <Col sm={2}></Col>
      </Row>
    </div>
  );
}

export default MyShopItemList;
