import React, { Fragment, useState } from "react";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";
import { logout } from "../redux/actions/auth";
import { Navbar, Button, Nav, Figure } from "react-bootstrap";
import BrandTitle from "../assets/GhorKuno Title.svg";

const NavBar = ({ logout, isAuthenticated }) => {
  const [redirect, setRedirect] = useState(false);

  const logout_user = () => {
    logout();
    setRedirect(true);
  };

  const guestNavLinks = () => (
    <Fragment>
      <li className="nav-item">
        <NavLink className="nav-NavLink fs-5" to="/login">
          Login
        </NavLink>
      </li>
      <li className="nav-item">
        <NavLink className="nav-NavLink fs-5" to="/signup">
          Sign Up
        </NavLink>
      </li>
    </Fragment>
  );

  const authNavLinks = () => (
    <Fragment>
      <li className="nav-item">
        <NavLink className="nav-NavLink fs-5" to="/user-info">
          UserInfo
        </NavLink>
      </li>
      <li className="nav-item">
        <a className="nav-NavLink fs-5" href="/login" onClick={logout_user}>
          Logout
        </a>
      </li>
    </Fragment>
  );
  {
    !isAuthenticated ? guestNavLinks() : authNavLinks();
  }
  return (
    <Navbar bg="light" variant={"light"} expand="lg">
      <Navbar.Brand href="/home">
        <Figure.Image
          width={200}
          height={40}
          alt="GhorKuno"
          src={BrandTitle}
          className="d-inline-block align-top"
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav variant="pills" className="fs-5 fw-bold ">
          <Nav.Item>
            <NavLink to="/home" className="m-2 ">
              Home
            </NavLink>
          </Nav.Item>
          <Nav.Item>
            <NavLink to="/drugs" className="m-2">
              Drugs
            </NavLink>{" "}
          </Nav.Item>
          <Nav.Item>
            <NavLink to="/shops" className="m-2">
              Shops
            </NavLink>{" "}
          </Nav.Item>
          <Nav.Item>
            <NavLink to="/cart" className="m-2">
              Cart
            </NavLink>{" "}
          </Nav.Item>
          <Nav.Item>
            <NavLink to="/contuct-us" className="m-2">
              Contact us
            </NavLink>{" "}
          </Nav.Item>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { logout })(NavBar);
